using System;using System.Collections.Generic;using System.Text.Json.Serialization;namespace MicroFeel.Yonyou.Api{	public class Job	{		///<Summary>		///岗位编码		///</Summary>		[JsonPropertyName("jobcode")]		public string Jobcode { get;set; }		///<Summary>		///岗位名称		///</Summary>		[JsonPropertyName("jobname")]		public string Jobname { get;set; }		///<Summary>		///所属部门		///</Summary>		[JsonPropertyName("depcode")]		public string Depcode { get;set; }		///<Summary>		///直接上级		///</Summary>		[JsonPropertyName("suporior")]		public string Suporior { get;set; }		///<Summary>		///成立日期		///</Summary>		[JsonPropertyName("builddate")]		public string Builddate { get;set; }		///<Summary>		///撤销日期		///</Summary>		[JsonPropertyName("abortdate")]		public string Abortdate { get;set; }		///<Summary>		///岗位序列		///</Summary>		[JsonPropertyName("jobseries")]		public string Jobseries { get;set; }		///<Summary>		///岗位等级		///</Summary>		[JsonPropertyName("jobrank")]		public string Jobrank { get;set; }		///<Summary>		///工作概要		///</Summary>		[JsonPropertyName("worksumm")]		public string Worksumm { get;set; }		///<Summary>		///职级分类		///</Summary>		[JsonPropertyName("jobrankclasscode")]		public string Jobrankclasscode { get;set; }		///<Summary>		///对应职级范围起始		///</Summary>		[JsonPropertyName("jobrankbegin")]		public string Jobrankbegin { get;set; }		///<Summary>		///对应职级范围结束		///</Summary>		[JsonPropertyName("jobrankend")]		public string Jobrankend { get;set; }		///<Summary>		///对应职等范围起始		///</Summary>		[JsonPropertyName("jobgradebegin")]		public string Jobgradebegin { get;set; }		///<Summary>		///对应职等范围结束		///</Summary>		[JsonPropertyName("jobgradeend")]		public string Jobgradeend { get;set; }	}}