using System;using System.Collections.Generic;using System.Text.Json.Serialization;namespace MicroFeel.Yonyou.Api{	public class Settlestyle	{		///<Summary>		///结算方式编码		///</Summary>		[JsonPropertyName("csscode")]		public string Csscode { get;set; }		///<Summary>		///结算方式名称		///</Summary>		[JsonPropertyName("cssname")]		public string Cssname { get;set; }		///<Summary>		///是否票据管理（0：否  1：是）		///</Summary>		[JsonPropertyName("bssflag")]		public string Bssflag { get;set; }		///<Summary>		///编码级次		///</Summary>		[JsonPropertyName("issgrade")]		public string Issgrade { get;set; }		///<Summary>		///是否末级（0：否  1：是）		///</Summary>		[JsonPropertyName("bssend")]		public string Bssend { get;set; }		///<Summary>		///对应票据类型(0:为空 1：现金支票 2：转账支票 3：普通支票 4：商业汇票)		///</Summary>		[JsonPropertyName("issbilltype")]		public string Issbilltype { get;set; }	}}