using System;using System.Collections.Generic;using System.Text.Json.Serialization;namespace MicroFeel.Yonyou.Api{	public class Cashflowitem	{		///<Summary>		///项目编码		///</Summary>		[JsonPropertyName("citemcode")]		public string Citemcode { get;set; }		///<Summary>		///项目名称		///</Summary>		[JsonPropertyName("citemname")]		public string Citemname { get;set; }		///<Summary>		///是否结算		///</Summary>		[JsonPropertyName("bclose")]		public string Bclose { get;set; }		///<Summary>		///项目大类编码		///</Summary>		[JsonPropertyName("citemccode")]		public string Citemccode { get;set; }		///<Summary>		///项目大类名称		///</Summary>		[JsonPropertyName("citemcname")]		public string Citemcname { get;set; }		///<Summary>		///方向		///</Summary>		[JsonPropertyName("cdirection")]		public string Cdirection { get;set; }		///<Summary>		///序号		///</Summary>		[JsonPropertyName("entry")]		public IList<Entry> entry { get;set; }		public class Entry		{						///<Summary>			///序号			///</Summary>			[JsonPropertyName("iid")]			public string Iid { get;set; }									///<Summary>			///数据类型			///</Summary>			[JsonPropertyName("bdatastyle")]			public string Bdatastyle { get;set; }						///<Summary>			///数据源			///</Summary>			[JsonPropertyName("cdatasource")]			public string Cdatasource { get;set; }						///<Summary>			///取数方式			///</Summary>			[JsonPropertyName("bdir")]			public string Bdir { get;set; }						///<Summary>			///科目名称			///</Summary>			[JsonPropertyName("ccode_name")]			public string CcodeName { get;set; }		}	}}